SELECT score, COUNT(*) as count FROM ratings AS r WHERE date > '2017-08-07 00:00:00.000000' AND EXISTS (SELECT * FROM jokecreations AS c WHERE c.joke_id=r.joke_id and c.user_id>2) AND EXISTS (SELECT * FROM jokes as j WHERE j.id=r.joke_id AND NOT X LIKE '% %' AND NOT Y LIKE '% %' AND NOT Z LIKE '% %') GROUP BY score ;